import * as React from 'react';
import {useSelector} from "react-redux";
import {withRouter} from "next/router";
import {Card, Col, Row} from "antd";
import Link from "next/link";
import Meta from "antd/es/card/Meta";
import AppLayout from "../components/AppLayout";
import SearchInput from "../components/SearchInput";
import {API_BASE_URL} from "../api/config";
import styled from "styled-components";

const StyledImgWrapper = styled.div`
  position: relative;
  display: block;
  overflow: hidden;
  height: 250px;
  &::before {
      content: "";
      padding-top: 100%;
      display: block;
  }
  img {
      position: absolute;
      width: 100%;
      height: 100%;
      object-fit: cover;
      top: 0;
      left: 0;
      bottom: 0;
      margin: auto;
  }
`;

const Search = ({ router: {query} }) => {

    const { name } = query;
    const restaurantList = useSelector(state => state.restaurant.restaurantList).filter((restaurant) => restaurant.name.includes(name));


    console.log(restaurantList);
    return (
        <AppLayout>
                <SearchInput/>
            <Row gutter={[10, 10]} style={{width: "100%"}}>
                <Col md={6} lg={8} />
        {restaurantList.map((e) => {
            console.log(e)
            return (
                <Col xs={24} md={12} lg={8}>
                    <StyledImgWrapper>
                        <Link href={{
                            pathname: '/restaurant',
                            query: { id: e.id},
                        }}
                        >
                            <a>
                                <Card
                                    hoverable
                                    cover={<img alt="example" src={API_BASE_URL + `/file/restaurant/img?fileNo=${e?.fileInfoList[0]?.fileNo}`}
                                                onClick={handleCardClick}/>}>
                                    <Meta title={e.name}/>
                                </Card>
                            </a>
                        </Link>
                    </StyledImgWrapper>
            </Col>)
            }
        )}
            <Col md={6} lg={8} />
            </Row>
        </AppLayout>
        );
};

export default withRouter(Search);
