import * as React from 'react';
import {
    SettingOutlined,
} from '@ant-design/icons';
import {signOutRequestAction} from "../reducers/user";
import {useDispatch} from "react-redux";
import styled from "styled-components";
import {Col, Menu, Row} from "antd";

const StyledHeader = styled.div`

    .ant-menu-submenu-title {

        padding-left : 0px;

    }


`;

const Header = () => {
    const dispatch = useDispatch();

    const handleSignOut = () => {
        localStorage.clear();
        dispatch(signOutRequestAction());
    }

    return (
        <>
            <StyledHeader style={{width:"100%", backgroundColor:"white"}}>
                <p style={{textAlign:'center'}}>BC로(路)</p>
                    <Menu mode="inline">
                        <Menu.SubMenu key="menu" icon={<SettingOutlined />} title="메뉴" style={{color:'black', border:"none"}}>
                            <Menu.ItemGroup style={{padding:'0px'}}>
                                <Menu.Item key="setting:1">개인정보</Menu.Item>
                                <Menu.Item key="setting:2">약속관리</Menu.Item>
                                <Link> <Menu.Item key="setting:2">로그아웃</Menu.Item></Link>
                            </Menu.ItemGroup>
                        </Menu.SubMenu>
                    </Menu>
            </StyledHeader>
        </>
    );
};

/*
 <ul style={{display:"flex", padding:"0px"}}>
    <StyledLi>
        <Link href="/profile"><span color={"black"}>My Page</span></Link>
    </StyledLi>
    <StyledLi>
        <Link href="/reservation"><span>Appointment</span></Link>
    </StyledLi>
    <StyledLi onClick={handleSignOut}>
        <span>Logout</span>
    </StyledLi>
</ul>
*
*
*
* */


export default Header;
