import * as React from 'react';
import {
    SettingOutlined,
} from '@ant-design/icons';
import {signOutRequestAction} from "../reducers/user";
import {useDispatch} from "react-redux";
import styled from "styled-components";
import {Col, Menu, Row} from "antd";

const StyledLi = styled.li`
 cursor: pointer;
  list-style-type: none;
  padding: 8px 5px;
  margin: 0 4px;
  span {
    color: grey;
    @media screen and (min-width: 501px) {
        font-size: 20px;
    }
    @media screen and (min-width: 310px) {
        font-size: 15px;
    }
    @media screen and (max-width: 310px) {
      font-size: 8px;
    }
    font-family: 'Readex Pro', sans-serif;
  }
`;

const StyledSetting = styled.span`
  margin-left: auto;
  color: grey;
  font-size: large;
  font-family: 'Readex Pro', sans-serif;
  @media screen and (min-width: 501px) {
    font-size: 20px;
  }
  @media screen and (min-width: 500px) {
    font-size: 13px;
  }
`;

const Header = () => {
    const dispatch = useDispatch();

    const handleSignOut = () => {
        localStorage.clear();
        dispatch(signOutRequestAction());
    }

    return (
        <>
            <div style={{width:"100%", backgroundColor:"white"}}>
                    <Menu mode="inline">
                        <Menu.SubMenu key="menu" icon={<SettingOutlined />} title="메뉴" style={{color:'black', border:"none"}}>
                            <Menu.ItemGroup>
                                <Menu.Item key="setting:1">개인정보</Menu.Item>
                                <Menu.Item key="setting:2">약속관리</Menu.Item>
                                <Menu.Item key="setting:2">로그아웃</Menu.Item>
                            </Menu.ItemGroup>
                        </Menu.SubMenu>
                    </Menu>
            </div>
        </>
    );
};

export default Header;
