import React, {
    useCallback,
    useRef,
    useState,
    useEffect
} from "react";
import {withRouter} from "next/router";
import AppLayout from "../components/AppLayout";

const ReviewRegister = () => {

    const [selectedFiles, setSelectedFiles] = useState('');

    const onClickHandler = event => {
        const formData = new FormData();
        formData.append(
            "uploadImages",
            this.state.selectedFiles,
            this.state.selectedFiles.name
        );
        const config = {
            headers: {
                "content-type": "multipart/form-data"
            }
        };
        // axios.post(`uploadAPI`, formData, config);
    };

    const fileChangedHandler = event => {
        const files = event.target.files;
        this.setState({
            selectedFiles: files
        });
    };

        return (
            <AppLayout>
                <StyledReivewRegister>

                    <div className="App" style={{ marginTop: "100px" }}>
                        <input type="file" multiple onChange={fileChangedHandler} />
                        <button onClick={onClickHandler}>저장하기</button>
                    </div>
                </StyledReivewRegister>
            </AppLayout>
        );
};

export default withRouter(ReviewRegister);
