import * as React from 'react';
import {Card, Row, Col} from 'antd';
import Link from "next/link";
import {useSelector} from "react-redux";
import {API_BASE_URL} from "../api/config";
import styled from "styled-components";


const StyledImgWrapper = styled.div`
  position: relative;
  display: block;
  border-radius: 10px;
  overflow: hidden;
  &::before {
      content: "";
      padding-top: 100%;
      display: block;
  }
  img {
      position: absolute;
      width: 100%;
      height: 100%;
      object-fit: cover;
      top: 0;
      left: 0;
      bottom: 0;
      margin: auto;
  }
`


export const Category = ({title, type}) => {
    const {Meta} = Card;

    let restaurantList = useSelector(state => state.restaurant.restaurantList);
    let count = 0;

    const handleCardClick = (e) => {

    };

    return (
        <>
            <h2>{title}</h2>
            {!type && <Row gutter={[10, 10]} style={{width: "100%"}}>
                {restaurantList?.map((e, idx) => {
                    if (idx < 6) {
                        return (<Col xs={24} md={12} lg={8}>

                                <>
                                    <StyledImgWrapper className=".img-wrapper">
                                        <Link href={{
                                            pathname: '/restaurant',
                                            query: {id: e.id},
                                        }}>
                                            <img
                                                src={API_BASE_URL + `/file/restaurant/img?fileNo=${e.fileInfoList[0]?.fileNo}`}
                                                onClick={handleCardClick}
                                            />
                                        </Link>
                                    </StyledImgWrapper>
                                    <Link href={{
                                        pathname: '/restaurant',
                                        query: {id: e.id},
                                    }}>
                                    <Meta title={e.name} description="www.aslwdf.com"/>
                                    </Link>
                                </>

                        </Col>);
                    } else {
                        return null;
                    }
                    })
                }
            </Row>
            }
            {type && <Row gutter={[10, 10]} style={{width: "100%"}}>
                {restaurantList?.map((e, idx) => {
                    if (count < 6 && (e.type === type)) {
                        return (<Col xs={24} md={12} lg={8}>
                            <Link href={{
                                pathname: '/restaurant',
                                query: {id: e.id},
                            }}>
                                <a>
                                    <StyledImgWrapper>
                                        <Meta title={e.name} description="www.aslwdf.com"/>
                                    </StyledImgWrapper>
                                </a>
                            </Link>
                        </Col>);
                    } else {
                        return null;
                    }
                    })
                }
            </Row>
            }
        </>
    );
}
