import * as React from 'react';
import Link from "next/link";
import {SignOutRequestAction} from "../reducers/user";
import {useDispatch} from "react-redux";
import styled from "styled-components";

const StyledNav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px;
  background-color: aliceblue;

`;

const StyledLi = styled.li`
  list-style-type: none;
  padding: 8px 12px;
  margin: 0 4px;

  span {
    color: grey;
    @media screen and (min-width: 401px) {
        font-size: 20px;
    }

    @media screen and (max-width: 300px) {
        font-size: 15px;

    }

    font-family: 'Readex Pro', sans-serif;
  }
`;

const StyledLogo = styled.span`
  color: grey;
  font-size: medium;
  font-weight: bold;

`;


const Header = () => {
    const dispatch = useDispatch();
        const handleLogout = () => {
        localStorage.clear();
        dispatch(SignOutRequestAction());
        location.href = '/';
    }

    return (
        <StyledNav>
            <div>
                <Link href="/"><StyledLogo>을지로 주변 맛집</StyledLogo></Link>
            </div>
            <div>
                <ul style={{display:"flex", padding:"0px"}}>
                <StyledLi>
                    <Link href="/profile"><span color={"black"}>My Page</span></Link>
                </StyledLi>
                <StyledLi>
                    <Link href="/reservation"><span>Appointment</span></Link>
                </StyledLi>
                <StyledLi onClick={handleLogout}>
                    <span>Logout</span>
                </StyledLi>
                </ul>
            </div>
        </StyledNav>
    );
};

export default Header;
