import React, { useState } from 'react';
import {AutoComplete, Button, Col, Row} from 'antd';
import {useSelector} from "react-redux";
import {SearchOutlined} from "@ant-design/icons";
import Link from "next/link";
const { Option } = AutoComplete;

const SearchInput = () => {
    const [name, setName] = useState("");
    const [result, setResult] = useState([]);
    const restaurantList = useSelector(state => state.restaurant.restaurantList);

    const handleSearch = (value) => {
        const res = restaurantList
            .filter((restaurant) => restaurant.id.includes(value+""))
            .map((restaurant)=> restaurant.id);
        setResult(res);
        setName(value)
    };

    const handleButton = (e) => {
        console.log(name);
    }

    return (
        <div style={{display:"flex"}}>
                <AutoComplete
                    dropdownMatchSelectWidth={252}
                    style={{
                        width: "100%"
                    }}
                    onSearch={handleSearch}
                >
                    {result.map((name, idx) => (
                        <Option key={idx} value={name}>
                            {name}
                        </Option>
                    ))}
                </AutoComplete>
                <Link href={{
                    pathname: '/restaurant',
                    query: { id:'123'},
                }}
                >
                    <Button style={{"marginLeft":"5px"}} size={"medium"} onClick={handleButton}><SearchOutlined/></Button>
                </Link>
        </div>
    );
};

export default SearchInput;
