import React, { useState } from 'react';
import {AutoComplete, Button, Col, Input, Row} from 'antd';
import {useSelector} from "react-redux";
import Icon, {SearchOutlined} from "@ant-design/icons";
const { Option } = AutoComplete;

const SearchInput = () => {

    const [result, setResult] = useState([]);
    const restaurantList = useSelector(state => state.restaurant.restaurantList);

    const handleSearch = (value) => {
        const res = restaurantList
            .filter((restaurant) => restaurant.id.includes(value+""))
            .map((restaurant)=> restaurant.id);
        setResult(res);
    };

    return (
        <Row>
            <Col xs={24} md={20}>
                <AutoComplete
                    dropdownMatchSelectWidth={252}
                    style={{
                        width: "90%"
                    }}
                    onSearch={handleSearch}
                >
                    {result.map((name, idx) => (
                        <Option key={idx} value={name}>
                            {name}
                        </Option>
                    ))}
                </AutoComplete>
            </Col>
            <Col xs={24} md={4}>
                <Button style={{ width:"60px", "marginLeft":"5px"}} size={"medium"}><SearchOutlined/></Button>
            </Col>
        </Row>
    );
};

export default SearchInput;
