import * as React from 'react';
import {
    DownOutlined,
    MailOutlined,
    SettingOutlined,
} from '@ant-design/icons';
import {signOutRequestAction} from "../reducers/user";
import {useDispatch} from "react-redux";
import styled from "styled-components";
import {Col, Menu, Row} from "antd";

const StyledLi = styled.li`
 cursor: pointer;
  list-style-type: none;
  padding: 8px 5px;
  margin: 0 4px;
  span {
    color: grey;
    @media screen and (min-width: 501px) {
        font-size: 20px;
    }
    @media screen and (min-width: 310px) {
        font-size: 15px;
    }
    @media screen and (max-width: 310px) {
      font-size: 8px;
    }
    font-family: 'Readex Pro', sans-serif;
  }
`;

const StyledSetting = styled.span`
  margin-left: auto;
  color: grey;
  font-size: large;
  font-family: 'Readex Pro', sans-serif;
  @media screen and (min-width: 501px) {
    font-size: 20px;
  }
  @media screen and (min-width: 500px) {
    font-size: 13px;
  }
`;

const Header = () => {
    const dispatch = useDispatch();

    const handleSignOut = () => {
        localStorage.clear();
        dispatch(signOutRequestAction());
    }

    return (
        <>
            <Row gutter={16}>
                <Col span={24}>
                    <Menu mode="inline" defaultSelectedKeys={['mail']}>
                        <Menu.SubMenu key="SubMenu" icon={<SettingOutlined />} title="Submenu">
                            <Menu.ItemGroup title="Item 1">
                                <Menu.Item key="setting:1">Option 1</Menu.Item>
                                <Menu.Item key="setting:2">Option 2</Menu.Item>
                            </Menu.ItemGroup>
                        </Menu.SubMenu>
                    </Menu>
                </Col>
            </Row>
        </>
    );
};

export default Header;
