import React, {
    useCallback,
    useRef,
    useState,
    useEffect
} from "react";
import {withRouter} from "next/router";
import AppLayout from "../components/AppLayout";

const StyledReivewRegister = styled.div`
  margin: 20px auto;
  width: 600px;
`;

const StyledButton = styled(Button)`
  display: block;
  margin: 5px auto;
  width: 300px;
  height: 40px;
  border-radius: 5px;
  font: 20px bold;
`;

const StyledTextArea = styled(TextArea)`
    margin-bottom: 10px;
`;

const ReviewRegister = () => {

    const [selectedFiles, setSelectedFiles] = useState('');

    const onClickHandler = event => {
        const formData = new FormData();
        formData.append(
            "uploadImages",
            this.state.selectedFiles,
            this.state.selectedFiles.name
        );
        const config = {
            headers: {
                "content-type": "multipart/form-data"
            }
        };
        // axios.post(`uploadAPI`, formData, config);
    };

    const fileChangedHandler = event => {
        const files = event.target.files;
        this.setState({
            selectedFiles: files
        });
    };

        return (
            <AppLayout>
                <StyledReivewRegister>

                    <div className="App" style={{ marginTop: "100px" }}>
                        <input type="file" multiple onChange={fileChangedHandler} />
                        <button onClick={onClickHandler}>저장하기</button>
                    </div>
                </StyledReivewRegister>
            </AppLayout>
        );
};

export default withRouter(ReviewRegister);
