import React, {
    useState,
} from "react";
import {withRouter} from "next/router";
import AppLayout from "../components/AppLayout";
import {useDispatch, useSelector} from "react-redux";
import {ReviewFileRegisterRequestAction} from "../reducers/review";
import styled from "styled-components";
import {Button, Upload} from "antd";
import TextArea from "antd/es/input/TextArea";
import { Rate } from 'antd';
import ImgCrop from 'antd-img-crop';

const StyledReviewRegister = styled.div`
  margin: 20px auto;
`;

const StyledTextArea = styled(TextArea)`
  height: 200px;
  margin-bottom: 10px;
`;

const StyledButton = styled(Button)`
  display: block;
  margin: 5px auto;
  width: 250px;
  height: 40px;
  border-radius: 5px;
  font: 20px bold;
`;

const ReviewRegister = ({ router: { query } }) => {

    const [selectedFiles, setSelectedFiles] = useState([]);

    const [starValue, setStarValue] = useState(1);
    const [textValue, setTextValue] = useState('');

    const [fileList, setFileList] = useState([]);

    const user_id = useSelector(state => state.user?.me?.id);
    const {restaurant_id} = query;
    const restaurantId = restaurant_id;
    const dispatch = useDispatch();

    const onTextChange = (e) => {
        setTextValue(e.target.value);
    }

    const handleStarChange = (starValue) => {
        setStarValue(starValue);
    }

    const onClickHandler = event => {
        const formData = new FormData();
        let cnt = 0;

        fileList.forEach(file => formData.append('files', file));

        for(let key in meta) {
            formData.append(key, meta[key]);
        }

        const data = {
            starpoint: starValue,
            content: textValue,
            userId: user_id,
            restaurantId,
        };

        const data2 = {
            file: formData,
        };

        dispatch(ReviewFileRegisterRequestAction(data2));
    };

    const uploadImage = async ({ fileList: newFileList }) => {
        setFileList(newFileList);
    }

    const onPreview = async file => {
        let src = file.url;
        if (!src) {
            src = await new Promise(resolve => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
            });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow.document.write(image.outerHTML);
    };

    return (
        <AppLayout>
            <StyledReviewRegister>
                <p style={{textAlign:'center', font:'30px bold'}}>리뷰 남기기</p>
                <form id="review">
                    <span>별점<Rate onChange={handleStarChange} value={starValue}/></span>
                    <StyledTextArea showCount maxLength={10000} onChange={onTextChange} />
                    <div className="App" style={{ marginTop: "100px" }}>
                        <ImgCrop rotate>
                            <Upload
                                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                listType="picture-card"
                                fileList={fileList}
                                onChange={uploadImage}
                                onPreview={onPreview}>
                            {fileList.length < 5 && '+ Upload'}
                            </Upload>
                        </ImgCrop>
                        <button type="button" onClick={onClickHandler}>저장하기</button>
                    </div>
                </form>
            </StyledReviewRegister>
        </AppLayout>
    );
};

export default withRouter(ReviewRegister);
