import * as React from 'react';
import AppLayout from "../components/AppLayout";
import { withRouter } from 'next/router';
import Map from "../components/Map";
import {useSelector} from "react-redux";
import {useEffect} from "react";

const Restaurant = ({ router: { query } }) => {

    let restaurantList = useSelector(state => state.restaurant.restaurantList);

    console.log(restaurantList.filter((e)=> e.id === query.id));

    const {lat, lng} = restaurantList.filter((e)=> e.id === query.id)[0];


    useEffect(()=>{
        const infoTitle = document.querySelectorAll('.infoTitle');
        infoTitle.forEach(function(e) {
            const w = e.offsetWidth + 10;
            const ml = w/2;
            e.parentElement.style.top = "82px";
            e.parentElement.style.left = "50%";
            e.parentElement.style.marginLeft = -ml+"px";
            e.parentElement.style.width = w+"px";
            e.parentElement.previousSibling.style.display = "none";
            e.parentElement.parentElement.style.border = "0px";
            e.parentElement.parentElement.style.background = "unset";
        });
    },[]);

    return (
        <AppLayout>
            <div>음식점 상세조회</div>
            <Map lat={lat} lng={lng}></Map>
        </AppLayout>
    );
};

export default withRouter(Restaurant);
